{
  "name": "otr-architecture-docs",
  "version": "1.0.0",
  "description": "OTR Digital Architecture Documentation",
  "scripts": {
    "build": "antora antora-playbook.yml",
    "build:local": "antora --fetch antora-playbook.yml",
    "serve": "npx http-server build/site -p 8080 -o",
    "dev": "npm run build:local && npm run serve"
  },
  "devDependencies": {
    "@antora/cli": "^3.1.9",
    "@antora/site-generator": "^3.1.9",
    "@asciidoctor/tabs": "^1.0.0-beta.6",
    "asciidoctor-kroki": "^0.18.1"
  }
}

